      it "assigns the requested act_load_list as @act_load_list" do
        ActLoadList.stub(:find) { mock_act_load_list(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:act_load_list).should be(mock_act_load_list)
      end

      it "redirects to the act_load_list" do
        ActLoadList.stub(:find) { mock_act_load_list(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(act_load_list_url(mock_act_load_list))
      end
    end

    describe "with invalid params" do
      it "assigns the act_load_list as @act_load_list" do
        ActLoadList.stub(:find) { mock_act_load_list(:update_attributes => false) }
        put :update, :id => "1"
